#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <limits.h>

#define DISTANCE 13

static const char *BIGNUM = "";

static const char *TESTNUM = "3456";

int *numToArray(const char *str);

double findNum(int *a, int len);

int main(){
	int len = strlen(BIGNUM);
	int *number = numToArray(BIGNUM);
	//printf("%d%d%d%d\n", *number, *(number+1), *(number+2), *(number+3));
	printf("%f\n", findNum(number, len));	
}

int *numToArray(const char *str){
	int len = strlen(str);
	int *arr = (int *)malloc(sizeof(int)*len);
	while(len >= 0){
		if(str[len-1]!='\n')
			*(arr+len-1) = (int)(str[len-1]-48);
		len--;
	}
	return arr;
}

double findNum(int *number, int len){
	int i = 0;
	double prod;
	double bigprod=0;
	while(i<len-DISTANCE){
 		prod = 1;
		int j=0;
		//printf("start: %d\t", *(number+i+j));
		for(;j<DISTANCE;j++){
			double oldnum = prod;
			double nextnum = *(number+i+j);
			prod *= nextnum;
			if(oldnum > prod && nextnum != 0)
				return -1; 
		}
		j--;
		//printf("end: %d\n", *(number+i+j));
		if(prod > bigprod)
			bigprod = prod;
		i++;
		
	}
	return bigprod;
}
